import React, { useState } from "react";
import "./EventCards.css";

const EventCards = () => {
  // Dummy event data
  const events = [
    {
      name: "Music Festival",
      description:
        "An unforgettable night of live music and entertainment. Enjoy performances from top artists and bands across various genres. This event will feature live DJ sets, food stalls, and an amazing light show. Don't miss out on the biggest festival of the year!",
      expiryDate: "2025-03-15",
      image: "data:image/jpeg;base64,",
    },
    {
      name: "Holiday Party",
      description:
        "Explore the latest trends in technology with industry leaders. Engage in panel discussions, keynote speeches, and hands-on workshops. This event will bring together top minds from the tech industry, including AI experts, software developers, and tech entrepreneurs.",
      expiryDate: "2025-04-10",
      image: "data:image/jpeg;base64,",
    },
    {
      name: "Ramadan",
      description:
        "A showcase of stunning artwork from talented artists worldwide. Experience breathtaking paintings, sculptures, and digital masterpieces. Art lovers will have a chance to interact with the artists, learn about their creative process, and even purchase exclusive pieces.",
      expiryDate: "2025-05-05",
      image: "data:image/jpeg;base64,",
    },
  ];

  const [expanded, setExpanded] = useState(Array(events.length).fill(false));

  const toggleExpand = (index) => {
    setExpanded((prev) => {
      const newExpanded = [...prev];
      newExpanded[index] = !newExpanded[index];
      return newExpanded;
    });
  };

  return (
    <div className="event-cards-container">
      {events.map((event, index) => (
        <div key={index} className={`event-card ${expanded[index] ? "expanded" : ""}`}>
          <div className="event-card-image">
            <img src={event.image} alt="Event" />
          </div>
          <div className="event-card-info">
            <h3>{event.name}</h3>
            <p>
              {expanded[index] ? event.description : `${event.description.substring(0, 50)}...`}
            </p>
            {event.description.length > 50 && (
              <button className="see-more-btn" onClick={() => toggleExpand(index)}>
                {expanded[index] ? "See Less" : "See More"}
              </button>
            )}
            <span className="event-card-date">Expires on: {event.expiryDate}</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default EventCards;
